css:
#uc-tournament-view {
  font-family: var(--fontFamily);
  color: var(--textColor);
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 9999;
  pointer-events: none;
}

#uc-tournament-view * {
  box-sizing: border-box;
}

/* ================================================ */
/* HEADER BAR */
/* ================================================ */

.tv-header {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 80px;
  background: linear-gradient(135deg, var(--primaryColor) 0%, var(--secondaryColor) 100%);
  box-shadow: var(--shadowLarge);
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 var(--spacingLarge);
  pointer-events: auto;
}

.tv-header::after {
  content: '';
  position: absolute;
  bottom: -4px;
  left: 0;
  right: 0;
  height: 4px;
  background: linear-gradient(90deg, var(--accentColor) 0%, transparent 50%, var(--accentColor) 100%);
  animation: pulse 2s ease-in-out infinite;
}

@keyframes pulse {
  0%, 100% { opacity: 0.5; }
  50% { opacity: 1; }
}

.tv-player-info {
  display: flex;
  align-items: center;
  gap: var(--spacingMedium);
  flex: 1;
}

.tv-player-name {
  font-size: var(--fontSizeLarge);
  font-weight: bold;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
  letter-spacing: 0.5px;
}

.tv-player-hp {
  display: flex;
  align-items: center;
  gap: var(--spacingSmall);
  background: rgba(0, 0, 0, 0.3);
  padding: var(--spacingSmall) var(--spacingMedium);
  border-radius: var(--borderRadius);
  backdrop-filter: blur(10px);
}

.tv-hp-icon {
  width: 24px;
  height: 24px;
  color: var(--dangerColor);
}

.tv-hp-bar {
  width: 120px;
  height: 12px;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 6px;
  overflow: hidden;
  position: relative;
}

.tv-hp-fill {
  height: 100%;
  background: linear-gradient(90deg, var(--dangerColor) 0%, var(--warningColor) 50%, var(--successColor) 100%);
  transition: width var(--transitionSpeed) ease;
  position: relative;
  box-shadow: 0 0 10px rgba(239, 68, 68, 0.5);
}

.tv-hp-text {
  font-size: var(--fontSizeSmall);
  font-weight: bold;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.8);
  min-width: 50px;
  text-align: center;
}

.tv-player-gold {
  display: flex;
  align-items: center;
  gap: var(--spacingSmall);
  background: rgba(0, 0, 0, 0.3);
  padding: var(--spacingSmall) var(--spacingMedium);
  border-radius: var(--borderRadius);
  backdrop-filter: blur(10px);
}

.tv-gold-icon {
  width: 20px;
  height: 20px;
  color: var(--warningColor);
}

.tv-gold-text {
  font-size: var(--fontSizeMedium);
  font-weight: bold;
  color: var(--warningColor);
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.8);
}

/* Sección central del header */
.tv-center-info {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: var(--spacingSmall);
}

.tv-turn-indicator {
  background: rgba(0, 0, 0, 0.4);
  padding: var(--spacingSmall) var(--spacingLarge);
  border-radius: var(--borderRadius);
  backdrop-filter: blur(10px);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
}

.tv-turn-label {
  font-size: var(--fontSizeSmall);
  color: var(--textSecondary);
  text-transform: uppercase;
  letter-spacing: 1px;
}

.tv-turn-number {
  font-size: var(--fontSizeLarge);
  font-weight: bold;
  color: var(--accentColor);
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.8);
}

.tv-current-player {
  font-size: var(--fontSizeSmall);
  color: var(--textSecondary);
  padding: 4px var(--spacingMedium);
  background: rgba(0, 0, 0, 0.3);
  border-radius: var(--borderRadius);
  backdrop-filter: blur(10px);
}

.tv-current-player.active {
  color: var(--accentColor);
  background: rgba(240, 147, 251, 0.2);
  box-shadow: 0 0 10px rgba(240, 147, 251, 0.3);
}

/* Información del oponente (lado derecho) */
.tv-opponent-info {
  display: flex;
  align-items: center;
  gap: var(--spacingMedium);
  flex: 1;
  justify-content: flex-end;
  flex-direction: row-reverse;
}

/* ================================================ */
/* ACTION LOG */
/* ================================================ */

.tv-action-log {
  position: absolute;
  top: 100px;
  left: var(--spacingLarge);
  width: 300px;
  max-height: calc(100vh - 200px);
  background: rgba(26, 26, 46, 0.95);
  border: 1px solid var(--borderColor);
  border-radius: var(--borderRadius);
  box-shadow: var(--shadowLarge);
  overflow: hidden;
  pointer-events: auto;
  backdrop-filter: blur(10px);
}

.tv-log-header {
  background: linear-gradient(135deg, var(--primaryColor) 0%, var(--secondaryColor) 100%);
  padding: var(--spacingMedium);
  font-weight: bold;
  font-size: var(--fontSizeMedium);
  border-bottom: 1px solid var(--borderColor);
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.tv-log-clear {
  background: rgba(0, 0, 0, 0.3);
  border: none;
  color: var(--textColor);
  padding: 4px 8px;
  border-radius: 4px;
  cursor: pointer;
  font-size: var(--fontSizeSmall);
  transition: background var(--transitionSpeed);
}

.tv-log-clear:hover {
  background: rgba(0, 0, 0, 0.5);
}

.tv-log-content {
  padding: var(--spacingSmall);
  max-height: calc(100vh - 280px);
  overflow-y: auto;
}

.tv-log-content::-webkit-scrollbar {
  width: 8px;
}

.tv-log-content::-webkit-scrollbar-track {
  background: rgba(0, 0, 0, 0.2);
}

.tv-log-content::-webkit-scrollbar-thumb {
  background: var(--primaryColor);
  border-radius: 4px;
}

.tv-log-content::-webkit-scrollbar-thumb:hover {
  background: var(--secondaryColor);
}

.tv-log-item {
  padding: var(--spacingSmall);
  margin-bottom: var(--spacingSmall);
  background: rgba(0, 0, 0, 0.3);
  border-radius: 4px;
  border-left: 3px solid var(--accentColor);
  font-size: var(--fontSizeSmall);
  animation: slideIn 0.3s ease;
}

@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateX(-20px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.tv-log-item.turn-start {
  border-left-color: var(--accentColor);
  background: rgba(240, 147, 251, 0.1);
}

.tv-log-item.card-played {
  border-left-color: var(--successColor);
}

.tv-log-item.spell-played {
  border-left-color: var(--primaryColor);
}

.tv-log-item.monster-destroyed {
  border-left-color: var(--dangerColor);
}

.tv-log-time {
  font-size: 0.75rem;
  color: var(--textSecondary);
  margin-bottom: 4px;
}

.tv-log-text {
  color: var(--textColor);
  line-height: 1.4;
}

.tv-log-empty {
  padding: var(--spacingLarge);
  text-align: center;
  color: var(--textSecondary);
  font-size: var(--fontSizeSmall);
}

/* ================================================ */
/* RESULT OVERLAY */
/* ================================================ */

.tv-result-overlay {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  backdrop-filter: blur(5px);
  pointer-events: auto;
  animation: fadeIn 0.5s ease;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.tv-result-container {
  background: linear-gradient(135deg, var(--surfaceColor) 0%, var(--backgroundColor) 100%);
  border: 2px solid var(--borderColor);
  border-radius: var(--borderRadius);
  padding: var(--spacingLarge) calc(var(--spacingLarge) * 2);
  box-shadow: var(--shadowLarge);
  text-align: center;
  animation: scaleIn 0.5s ease;
  min-width: 400px;
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.8);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.tv-result-title {
  font-size: 3rem;
  font-weight: bold;
  margin-bottom: var(--spacingLarge);
  text-shadow: 3px 3px 6px rgba(0, 0, 0, 0.8);
}

.tv-result-title.victory {
  color: var(--successColor);
  text-shadow: 0 0 20px rgba(16, 185, 129, 0.5);
}

.tv-result-title.defeat {
  color: var(--dangerColor);
  text-shadow: 0 0 20px rgba(239, 68, 68, 0.5);
}

.tv-result-title.draw {
  color: var(--warningColor);
  text-shadow: 0 0 20px rgba(245, 158, 11, 0.5);
}

.tv-result-stats {
  display: flex;
  flex-direction: column;
  gap: var(--spacingMedium);
  margin-top: var(--spacingLarge);
}

.tv-result-stat {
  display: flex;
  justify-content: space-between;
  padding: var(--spacingSmall) var(--spacingMedium);
  background: rgba(0, 0, 0, 0.3);
  border-radius: 4px;
}

.tv-result-stat-label {
  color: var(--textSecondary);
}

.tv-result-stat-value {
  color: var(--textColor);
  font-weight: bold;
}

/* ================================================ */
/* COMPACT MODE */
/* ================================================ */

#uc-tournament-view.compact .tv-header {
  height: 60px;
}

#uc-tournament-view.compact .tv-player-name {
  font-size: var(--fontSizeMedium);
}

#uc-tournament-view.compact .tv-hp-bar {
  width: 80px;
}

#uc-tournament-view.compact .tv-action-log {
  width: 250px;
}

#uc-tournament-view.compact .tv-log-header {
  padding: var(--spacingSmall);
  font-size: var(--fontSizeSmall);
}

/* ================================================ */
/* ANIMATIONS OFF */
/* ================================================ */

#uc-tournament-view.no-animations * {
  animation: none !important;
  transition: none !important;
}

/* ================================================ */
/* RESPONSIVE */
/* ================================================ */

@media (max-width: 1280px) {
  .tv-action-log {
    width: 250px;
  }

  .tv-player-info {
    gap: var(--spacingSmall);
  }
}

@media (max-width: 960px) {
  .tv-action-log {
    display: none;
  }

  .tv-header {
    padding: 0 var(--spacingMedium);
  }
}
